/*	Garry's original AccessorFunc is a horrible implementation of forcing vars and making entering data through accessors and modifiers secure, it only supports bools, strings and numbers, its pretty limited.	We use this since I decided not to override garry's ass on AccessorFunc and screw up other mods*/function AccessorFuncForce(tbl,strVar,strFunc, strType, valDefault)	if __type(tbl) ~= "table" or type(strVar) ~= "string" then return end	strFunc = FORCE(strFunc,strVar) -- if strFunc isn't a string then we put strVar as the name	tbl["Get"..strFunc] = function (self) return self[strVar] end	if type(strType) == "string" then		tbl["Set"..strFunc] = function (self,val)			if type(val) == strType then				self[strVar] = val			end		end	else		tbl["Set"..strFunc] = function (self,val) self[strVar] = val end	end	end--[[ Creates EnableVar and DisableVar functions ]]function AllowFunc(tbl,strVar, strFunc, bDefault)	strFunc, bDefault = FORCE(strFunc,strVar), FORCE(bDefault,true)	tbl["Allow"..strFunc] = function (self,bAllow) self[strVar] = FORCE(bAllow,bDefault) endend