--[[module( "cvars", package.seeall )local ConVars = {}function GetConVarCallbacks( name, CreateIfNotFound ) 	local Tab = ConVars[ name ] 	if ( CreateIfNotFound && !Tab ) then 		Tab = {} 		ConVars[ name ] = Tab 	end 	return Tabendfunction OnConVarChanged(strName,strOld,strNew) 	local tblCallbacks = ConVars[strName] 	if not tblCallbacks then return end 	for k, v in pairs( tblCallbacks ) do 		local bOk, valReturn = pcall( v.callback,strName,strOld,strNew, v.cvar )		if not bOk then			ErrorNoHalt("OnConVarChanged callback failed : '"..tostring(valReturn).."'\n")		end 	endendfunction AddChangeCallback( strName, funcCallback )	ConVars[strName] = ConVars[strName] or {} 	table.insert(ConVars[strName], {callback=funcCallback,cvar=GetConVar(strName) or cc.Get(strName)})end]]