local numTimeScaleInv = 1.0cvars.AddChangeCallback("phxlib_timescale",function (cvar, old, new)	numTimeScaleInv = 1.0/cvar:GetFloat() -- or tonumber(new)end)local meta = FindMetaTable("Weapon")local oldSetPrimary = meta.SetNextPrimaryFirefunction meta:SetNextPrimaryFire(clkTime)	if numTimeScaleInv ~= 1 then		local cTime = CurTime()		clkTime = cTime + (clkTime-cTime)*numTimeScaleInv	end	oldSetPrimary(self,clkTime)endlocal oldSetSecondary = meta.SetNextSecondaryFirefunction meta:SetNextSecondaryFire(clkTime)	if numTimeScaleInv ~= 1 then		local cTime = CurTime()		clkTime = cTime + (clkTime-cTime)*numTimeScaleInv	end	oldSetSecondary(self,clkTime)end