local mat_HeatWave = Material("sprites/heatwave")function OBJ:Predict(tblSetup)	self.Origin = tblSetup.StartPos	self.Entity:SetAngles(tblSetup.Angle)	self.Speed = tblSetup.Speed*16 -- FPS TO UNITS per second	self.StartTime = CurTime()	self.Filter = {}	self.Shooter = LocalPlayer()endfunction OBJ:Receive(um)	self.Origin = um:ReadVector()	self.Entity:SetAngles(um:ReadAngle())	self.Speed = um:ReadLong()*16	self.StartTime = um:ReadFloat()	self.Shooter = um:ReadEntity()	self.Filter = {}endfunction OBJ:Initialize()	self.Entity:SetModel(strModel)	self.Entity:SetPos(self.Origin)	--self:SetVelocity(self.Entity:GetAngles():Forward()*self.Speed)	self.traceData = {filter = self.Filter,mask=MASK_SHOT}	--self.RicochetCount = 0endlocal tblMAT_Translate = {	"Impact.Concrete",	"Impact.Glass",	"Impact.Metal",	"Impact.Sand",	"Impact.Wood"}function OBJ:Think()end--local mat_BloodTrail = Material("bloodtrail")--local mat_TunnelTrail = Material("TunnelTrail")--local mat_penetrate_noz = Material("utility/noz")function OBJ:Render()		render.SuppressEngineLighting(true)			self.Entity:DrawModel()		render.SuppressEngineLighting(false)	--implement heatwave effect laterend