OBJ.Config = {	Tracer = "sprites/bluelight1",	MaxPenetrate = 50}function OBJ:CalcWorldPenetration(tr,numMax)	local tr2 = util.TraceLine({start=tr.HitPos + tr.Normal*numMax,endpos=tr.HitPos,filter=self.Filter,mask=MASK_NPCWORLDSTATIC})	if tr2.Fraction == 0 then return false end	return true, tr2.HitPosendfunction OBJ:CalcTrajectory()	while true do		local tr = util.TraceLine({start=self.Entity:GetPos(),endpos=self.Entity:GetPos() + self.Entity:GetAngles():Forward()*16000,filter=self.Filter,mask=MASK_SHOT})		if tr.Hit then			if tr.HitWorld then				local bPenetrate, vecExit = self:CalcWorldPenetration(tr,self.Config.MaxPenetrate)				if bPenetrate then					self.Entity:SetPos(vecExit)				end			elseif ValidEntity(tr.Entity) then				self.Entity:SetPos(tr.HitPos)				tr.Entity:TakeDamage(100,self.Shooter,self.Shooter:GetActiveWeapon())			end		end	endend