function OBJ:Initialize()	self.Entity:PhysicsInit(SOLID_VPHYSICS)endfunction OBJ:Spawn()	self.Entity:Spawn()endfunction OBJ:DriverEnter(objPl)	self.Driver = objPl	self.Driver:SetScriptedVehicle(self.Entity)endfunction OBJ:DriverExit()	self.Driver:SetScriptedVehicle(nil)	self.Driver = nilendfunction OBJ:OnRemove()	umsg.Start("sveh_remove",0)		umsg.Short(self.Index)	umsg.End()	if self.Driver then	self:DriverExit() endendfunction OBJ:Tick()	end