function EFFECT:Init(data)	self.Player = data:GetEntity()	self.LastRender = 0	if ValidEntity(self.Player) then		self.Player.VisibilityEffect = self.Entity	endendfunction EFFECT:Think()	if not ValidEntity(self.Player) then return false end	if self.Player:Alive() then		self.Entity:SetPos(self.Player:GetPos())		self.Entity:SetRenderBoundsWS(self.Player:GetPos() + self.Player:OBBMins(),self.Player:GetPos() + self.Player:OBBMaxs())	else		local rag = self.Player:GetRagdollEntity()		if ValidEntity(rag) then			self.Entity:SetPos(rag:GetPos())			self.Entity:SetRenderBoundsWS(rag:GetPos() + rag:OBBMins(),rag:GetPos() + rag:OBBMaxs())		end	end	return trueendfunction EFFECT:Render()	self.LastRender = CurTime()	gamemode.Call("PlayerRender",self.Player)end